import React, { useState, useEffect } from "react";

const Tourls = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // จำลองการโหลดข้อมูล ใช้ setTimeout เพื่อเปลี่ยนสถานะการโหลด
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 3000); // เปลี่ยนสถานะเป็น false หลังจาก 3 วินาที

    return () => clearTimeout(timer); // เคลียร์ timer เมื่อ component ถูก unmount
  }, []);

  return (
    <div>
      {isLoading ? (
        <div>
          <h1>LOADING..........</h1>
        </div>
      ) : (
        <div>
          <h1>Data Loaded</h1>
        </div>
      )}
    </div>
  );
};

export default Tourls;
